definition.script:
def RUBY_REPOS = RUBY_REPOS.split(" ")
def fail = false
def repos_affected = "Repos affected:\n"
def repos_not_affected = "Repos examined:\n"
def audit_files = [".bundle-auditrc", "Gemfile.lock", "Gemfile"]

node() {
    try {
        stage('Download dependencies files') {
            for (int i = 0; i < RUBY_REPOS.size() ; i++) {
                sh "mkdir -p ./${RUBY_REPOS[i]}"
                for (int j = 0; j < audit_files.size(); j++) {
                    sh (script: "curl https://raw.githubusercontent.com/EleazarWorkshare/${RUBY_REPOS[i]}/master/${audit_files[j]} -o ./${RUBY_REPOS[i]}/${audit_files[j]} -f", returnStatus: true)
                }
            }
        }
        stage('Generate Audited repo report') {
            for (int i = 0; i < RUBY_REPOS.size() ; i++) {
                def ignore = ""
                def audit_exceptions = audit_files.first()
                if (fileExists("./${RUBY_REPOS[i]}/${audit_exceptions}")){
                    ignore = readFile encoding: 'UTF-8', file: "./${RUBY_REPOS[i]}/${audit_exceptions}"
                    ignore = ignore.split("\n").join(" ")
                }
                def out = sh (script: "sudo docker run --rm -i -v /var/lib/docker/volumes/entorno_data-jenkins/_data/workspace/analisis-dependencias-ruby/${RUBY_REPOS[i]}/Gemfile.lock:/Gemfile.lock -v /var/lib/docker/volumes/entorno_data-jenkins/_data/workspace/analisis-dependencias-ruby/${RUBY_REPOS[i]}/Gemfile:/Gemfile entorno_ruby-tool:latest bundle-audit check --update ${ignore} || true", returnStdout: true)
                writeFile encoding: 'UTF-8', file: "./${RUBY_REPOS[i]}/report.txt", text: "${out}"
            }
        }
        stage('Generate html report') {
            def index = "<h1><b>Ruby services</b></h1>"
            for (int i = 0; i < RUBY_REPOS.size() ; i++) {
                def report = readFile encoding: 'UTF-8', file: "./${RUBY_REPOS[i]}/report.txt"
                if(!report.contains("No vulnerabilities found")){
                    index += "<h3><b>${RUBY_REPOS[i]}</b></h3><p>" + report.replaceAll("(\r\n|\n)", "<br />") + "<br /><br /></p>"
                    repos_affected += "${RUBY_REPOS[i]}\n"
                    fail = true
                } else {
                    repos_not_affected += "${RUBY_REPOS[i]}\n"
                }
            }
            writeFile encoding: 'UTF-8', file: "./reports/index.html", text: "${index}"
            publishHTML(
                [allowMissing: false,
                alwaysLinkToLastBuild: true,
                keepAll: true,
                reportDir: 'reports',
                reportFiles: 'index.html',
                reportName: 'HTML Report'])
        }
//        stage('Notify generated report') {
//            if (fail == true){
//               slackSend channel: 'static-analysis',
//                      color: '#FF0000',
//                      message: "[FAILED] Ruby analysis - Vulnerabilities found!\n${repos_affected}\nHTML report:\nhttps://{URL_TO_THE_REPORT}/${env.BUILD_NUMBER}/HTML_Report/",
//                      tokenCredentialId: 'Slack-Jenkins-Ruby'
//            } else {
//                slackSend channel: 'static-analysis',
//                      color: '#008000',
//                      message: "[OK] Ruby analysis - No vulnerabilities found!\n${repos_not_affected}",
//                      tokenCredentialId: 'Slack-Jenkins-Ruby'
//            }
//        }
    } catch(Exception e) {
        print(e)
        currentBuild.result = 'FAILURE'
    } finally {
        //deleteDir()
        if (fail == true){
            currentBuild.result = 'FAILURE'
        }
    }
}
