definition.script:
def DOCKER_REPOS = DOCKER_REPOS.split(" ")
node() {
    try{
        stage("Docker image security scan and report") {
            for (i = 0; i < DOCKER_REPOS.size(); i++)
            {
                withEnv(["repos=${ DOCKER_REPOS[i] }"]) {
                    sh 'sudo docker run --rm -t -v entorno_reports:/go/src/github.com/jgsqware/clairctl/reports -v /var/run/docker.sock:/var/run/docker.sock:ro --net entorno_default --link clair_clair:clair -p 57330:57330 entorno_clairctl bash -c "cd /go/src/github.com/jgsqware/clairctl/ ; ./clairctl analyze --log-level info --local $repos ; ./clairctl report --log-level info --local $repos"'
                }
            }
        }
        stage("Generate HTML report for Jenkins") {
            def html_list = []
            sh 'mkdir reports'
            for (i = 0; i < DOCKER_REPOS.size(); i++)
            {
                withEnv(["repos=${ DOCKER_REPOS[i] }"]) {
                    sh 'sudo mv /reports/html/analysis-$repos-latest.html $WORKSPACE/reports/$repos-latest.html'
                    html_list.add("$repos-latest.html")
                }
            }
            publishHTML(
                [allowMissing: false,
                alwaysLinkToLastBuild: true,
                keepAll: true,
                reportDir: 'reports',
                reportFiles: html_list.join(","),
                reportName: 'HTML Report'])
        }
//        stage("Send notification") {
//            def repos_list = []
//            images.each{ repo, tag -> repos_list.add("${repo}:${tag}") }
//            def repos_string = repos_list.join("\n")
//            slackSend channel: 'base-image-analysis',
//               color: '#0077FF',
//                message: "[GENERATED] Docker images analysis:\n${repos_string}\n HTML report:\nhttps://dev-deploy.workshare.com/view/Static%20Analysis/job/clair-analysis/${env.BUILD_NUMBER}/HTML_Report/",
//                tokenCredentialId: 'Slack-Jenkins-Clair-Analysis'
//        }
    } catch(error) {
        // if any exception occurs, mark the build as failed
        currentBuild.result = 'FAILURE'
        throw error
    } finally {
        deleteDir()
    }
}
